#include <iostream>using namespace std;int main() {    int num, flag=1;    cout << "Enter an Integer Number : ";    cin >> num;    if(num<=1) {        cout << endl << num << " is not a Prime Number..." << endl;    } else {        for(int i=2; i<num; i++) {            if(num%i==0) {                flag = 0;                break;            }        }    }    if(flag) {        cout << endl << num << " is a Prime number..." << endl;    } else {        cout << endl << num << " is not a Prime number..." << endl;    }    return 0;}